import { Button, TextField } from "@mui/material";
import React, {useState} from "react";
import styled from 'styled-components'

export default function Todo() {
    const [todo, setTodo] = useState('')

    const handleChange = e => {
        e.preventDefault()
        const val = e.target.value
        setTodo(val)
    }

    const submitForm = e => {
        e.preventDefault()
        setTodo(e.target.value)
    }

    return (
    <form onSubmit={submitForm} method="POST">
        <TodoDiv>
            {/* <TextField id="outlined-basic" label="Todo..." variant="outlined" /> */}
            {/* <Button variant="outlined" onChange={handleChange}>추가</Button> */}
            <input type='text' />
            <input type='submit' value="추가"/><br/>
            <p>할 일: {todo}</p>
            <Button variant="outlined" onClick={() => setTodo(todo - 1)}>삭제</Button>
        </TodoDiv>
    </form>
    )
}

const TodoDiv = styled.div` text-align: center; `
